import './Vin.css';
import axios from 'axios';
import { useState } from 'react';
import React from 'react';

//5UXWX7C5*BA

function Vin() {
    const [userValue, setUV] = useState("");
    const [final, setFinal] = useState([]);
    let myfile = '';
    let list = {};
    axios.defaults.baseURL = "https://vpic.nhtsa.dot.gov/api/vehicles/DecodeVin/";

    //called on change of the varaible  
    const getIn = (event)=> {
        setUV(event.target.value);
    }

    //updates final list
    //called on press of enter
    const uval = (event)=>{
      console.log(userValue);
      axios({
            url: userValue+"?format=json"
        })
        .then(function(response){
            myfile = (response.data.Results);
            console.log(myfile);
            })
        .then(function(){
            fup();
        })
    }

    const fup = ()=>{
      let fv = ['Make','Model','Model Year','Manufacturer Name', 'Plant City','Vehicle Type','Plant Country', 'Plant State', 'Body Class', 'Engine Number of Cylinders','Drive Type'];
      for(let i = 0; i < myfile.length; i++){
          for(let j = 0; j < fv.length; j++){
              if(myfile[i].Variable=== fv[j]){
                  let newVal;
                  if(myfile[i].Value){
                      newVal = (myfile[i].Value).toString();
                  }
                  else{
                      newVal = "N/A";
                  }
                  //list = [...list,newVal];
                  list[fv[j]] = newVal;
              }
          }
      }
      setFinal(list);
      console.log(list);
      getData(list);

    } 

    const getData = async (nhtsaRes) =>{
      let suffix = "/"+"JAPAN"+"/"+"TSUTSUMI"+"/"+"AICHI"+"/"+"TOYOTA MOTOR CORPORATION"+"/"+"TOYOTA"+"/"+"PASSENGER CAR"+"/";
      let response = await fetch('http://127.0.0.1:8000/api/el'+suffix);
      let data = await response.json();
      console.log(data);
      // /<_ctry>/<_city>/<_state>/<_mfr_>/<_make>/')
      //JAPAN TSUTSUMI AICHI TOYOTA MOTOR CORPORATION TOYOTA PASSENGER CAR
      //pprint(get_related(ctry=pctry, state=pstate, city=pcity, mfr_=pmfr, make=pmake, type=ptype))
    }

    //['Make','Model','Model Year','Manufacturer Name', 'Plant City','Vehicle Type','Plant Country', 'Plant State', 'Body Class', 'Engine Number of Cylinders','Drive Type'];

    return (
      <div className="App">
        <header className="App-header">
          <p>
            Car Information Analyzer
          </p>
          <p>
              HI! YOUR CAR IS A {final['Make']} {final['Model']} FROM {final['Model Year']} AND IS TRADITIONALLY USED AS A {final['Vehicle Type']}. 
              IT IS A {final['Body Class']} with {final['Engine Number of Cylinders']} CYLINDERS AND DRIVES AS A {final['Drive Type']} IT WAS MADE IN THE CITY OF {final['Plant City']} WHICH IS LOCATED IN {final['Plant State']}, {final['Plant Country']}.

          </p>
          <a
            className="App-link"
            href="https://vpic.nhtsa.dot.gov/api/"
            target="_blank"
            rel="noopener noreferrer"
          >
            Data Source
          </a>
          <label className="Vin"> Enter Your Vin:
              <input type="text" onChange={getIn} value={userValue} />
              <button title="submit" onClick={uval} >Submit</button>
          </label>
        </header>
      </div>
    );
  }
  export default Vin;
