import './App.css';
import bst from './components/bst';
import hm from './components/hm';
import axios from 'axios';
import { useState } from 'react';
import React from 'react';

function App() {
  const [userValue, setUV] = useState('');
  const [myObj, setmyObj] = useState('');
  const [final, setFinal] = useState([]);
  axios.defaults.baseURL = "https://vpic.nhtsa.dot.gov/api/vehicles/DecodeVin/";

  //called on change of the varaible  
  const getIn = (event)=> {
      setUV(() => event.target.value);
      setUV();
      console.log(userValue);
  }

  //called on press of enter
  const uval = (event)=>{
      axios({
          url: "5UXWX7C5*BA?format=json"
      })
      .then(function(response){
          myObj = response.data.Results;
          })
      .then(function(){
          for(let i = 0; i < myObj.length; i++){
              if(myObj[i].Variable=== 'Make'){
                  setFinal((prevFinal) => [...prevFinal, (myObj[i].Value).toString()]);
                  setFinal(final);
              }
              else if(myObj[i].Variable=== 'Model')
              {
                  setFinal((prevFinal) => [...prevFinal, (myObj[i].Value).toString()]);
                  setFinal(final);
              }
          }
      })
    }

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Car Information Analyzer
        </p>
          The make is {final[0]} and the model is {final[1]}
        <a
          className="App-link"
          href="https://vpic.nhtsa.dot.gov/api/"
          target="_blank"
          rel="noopener noreferrer"
        >
          Data Source
        </a>
        <button title="BST" className="BstB" onClick={bst}> BST </button>
        <button title="Hash Map" className="HmB" onClick={hm}> HashMap </button>
        <label className="Vin"> Enter Your Vin:
            <input type="text" onChange={getIn}/>
        </label>
      </header>
    </div>
  );
}

export default App;
